export default()=>{Array.from(document.querySelectorAll("[data-header]")).forEach((e=>{var r=e.querySelector("[data-header-content]"),a=e.querySelector("[data-header-close]");e.querySelector("[data-header-open]").addEventListener("click",(()=>{r.classList.add("cli-mobile-menu")})),a.addEventListener("click",(()=>{r.classList.remove("cli-mobile-menu")}));var t=Array.from(e.querySelectorAll("[data-header-menu] a")),l=window.location.href,d=t.find((e=>e.href===l));d&&d.classList.add("active"),Array.from(e.querySelectorAll("[data-header-menu] button")).forEach((e=>{e.addEventListener("click",(()=>{e.closest(".cli-mobile-menu")&&e.parentElement.querySelector("[data-submenu]").classList.toggle("cli-header-menu__dropdown_visible")}))}))}))};